function calculateDelivery(){const e=document.getElementById("fromCity").value,t=document.getElementById("toCity").value,o=document.getElementById("packageSize").value;e&&t&&o?fetch("https://shift-backend.onrender.com/api/delivery/calc",{method:"POST",headers:{"Content-Type":"application/json"},body:JSON.stringify({fromCity:e,toCity:t,packageSize:o})}).then((e=>e.json())).then((e=>{console.log("Результат расчета доставки:",e)})).catch((e=>{console.error("Ошибка при расчете доставки:",e)})):alert("Пожалуйста, заполните все поля")}async function showCitiesList(e){const t=document.getElementById(e);try{const e=await fetch("https://shift-backend.onrender.com/delivery/points"),o=await e.json();console.log("Список городов:"),o.points.forEach((e=>{console.log(e.name)})),t.innerHTML="";const n=document.createElement("option");n.value="",n.text="Выберите город",t.appendChild(n),o.points.forEach((e=>{const o=document.createElement("option");o.value=e.id,o.text=e.name,t.appendChild(o)}))}catch(e){console.error("Ошибка при загрузке городов:",e)}}async function showPackageList(e){const t=document.getElementById(e);try{const e=await fetch("https://shift-backend.onrender.com/delivery/package/types"),o=await e.json();console.log("Список размеров посылки:"),o.packages.forEach((e=>{console.log(e.name)})),t.innerHTML="";const n=document.createElement("option");n.value="",n.text="Выберите размер посылки",t.appendChild(n),o.packages.forEach((e=>{const o=document.createElement("option");o.value=e.id,o.text=e.name,t.appendChild(o)}))}catch(e){console.error("Ошибка при загрузке размеров посылки:",e)}}document.addEventListener("DOMContentLoaded",(()=>{showCitiesList("fromCity"),showCitiesList("toCity"),showPackageList("packageSize")}));